import React from 'react';
import styled from 'styled-components';

const Input = styled.input.attrs(props => ({
    size: props.small ? 5 : undefined,
    type: "email",
    placeholder: "Email Address",
    name: "email"
  }))`
    border-radius: 6px;
    border: 2px solid red;
    display: block;
    margin: 0 0 1em;
    padding: 6px
`

export function EmailStyled({
    children
}) {
    return (
        <Input>
            {children}
        </Input>
    )
}
