import React, { Component } from 'react';

class Modal extends Component {

    state = {
        show: false
    };

    showModal = e => {
        this.setState({
            show: true
        });
    }
    render() {
        return (
            <button onClick={
                e => {
                    this.showModal();
                }
            }>
                show modal
            </button>
        )
    }
}

export default Modal;
