import React, { useState, Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createProfile, getCurrentProfile } from '../../../redux/profile';
import { Link, withRouter } from 'react-router-dom';

const EditProfile = ({
    profile: {
        profile,
        loading
    },
    createProfile,
    getCurrentProfile,
    history
}) => {
    const [formData, setFormData] = useState({
        status: '',
        bio: '',
        twitter: '',
        facebook: '',
        linkedin: '',
        youtube: '',
        instagram: ''
    });

    const [displaySocials, toggleSocials ] = useState(false);

    useEffect(() => {
        getCurrentProfile();
        setFormData({
            bio: loading || !profile.bio ? '' : profile.bio,
            twitter: loading || !profile.social ? '' : profile.social.twitter,
            facebook: loading || !profile.social ? '' : profile.social.facebook,
            linkedin: loading || !profile.social ? '' : profile.social.linkedin,
            youtube: loading || !profile.social ? '' : profile.social.youtube,
            instagram: loading || !profile.social ? '' : profile.social.instagram,
        })
    }, [loading, getCurrentProfile]);

    const {
        location,
        bio,
        twitter,
        facebook,
        linkedin,
        youtube,
        instagram,
    } = formData;

    const onChange = e => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        })
    }

    const onSubmit = e => {
        e.preventDefault();
        createProfile(
            formData,
            history,
            true)
    }


    return (
        <Fragment>
            <h1 className="large text-primary">
                New Profile
            </h1>
            <p className="lead">
                Add some profile information
            </p>
            <small>* = required field</small>
            <form 
            className="form"
            onSubmit = {
                e => onSubmit(e)
            }>
                <div className="form-group">
                    <input type="text" 
                    placeholder="Location" 
                    name="location"
                    value={location} 
                    onChange={e => onChange(e)} />
                    <small className="form-text"
                        >City & state suggested (eg. Boston, MA)</small
                    >
                </div>
                <div className="form-group">
          <textarea placeholder="A short bio of yourself" 
          name="bio"
          value={bio} 
          onChange={e => onChange(e)}></textarea>
          <small className="form-text">Tell us a little about yourself</small>
        </div>

        <div className="my-2">
          <button 
          type="button" 
          className="btn btn-light"
          onClick={() => toggleSocials(!displaySocials)}>
            Add Social Network Links
          </button>
          <span>Optional</span>
        </div>

        {displaySocials && <Fragment>
            <div className="form-group social-input">
          <i className="fab fa-facebook fa-2x"></i>
          <input type="text" 
          placeholder="Facebook URL" 
          name="facebook" 
          value={facebook} 
          onChange={e => onChange(e)}/>
        </div>

        <div className="form-group social-input">
          <i className="fab fa-youtube fa-2x"></i>
          <input type="text" 
          placeholder="YouTube URL" 
          name="youtube" 
          value={youtube} 
          onChange={e => onChange(e)}/>
        </div>

        <div className="form-group social-input">
          <i className="fab fa-linkedin fa-2x"></i>
          <input type="text" 
          placeholder="Linkedin URL" 
          name="linkedin" 
          value={linkedin} 
          onChange={e => onChange(e)}/>
        </div>

        <div className="form-group social-input">
          <i className="fab fa-instagram fa-2x"></i>
          <input type="text" 
          placeholder="Instagram URL" 
          name="instagram" 
          value={instagram} 
          onChange={e => onChange(e)}/>
        </div>
        <div className="form-group social-input">
          <i className="fab fa-twitter fa-2x"></i>
          <input type="text" 
          placeholder="Twitter URL" 
          name="twitter" 
          value={twitter} 
          onChange={e => onChange(e)}/>
        </div>
        </Fragment>}
        <input type="submit" className="btn btn-primary my-1" />
        <Link to="/profile" className="btn btn-light my-1" >Go Back</Link>
            </form>
        </Fragment>
    )
}

EditProfile.propTypes = {
    createProfile: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    getCurrentProfile: PropTypes.func.isRequired,
}

const mapStateToProps = state => ({
    profile: state.profile
})

export default connect(
    mapStateToProps, {
        createProfile,
        getCurrentProfile
    }
)(withRouter(EditProfile));
