/**
 * apeman react package for footer component.
 * @class ApFooter
 */

'use strict'

import React, {PropTypes as types} from 'react'
import classnames from 'classnames'

/** @lends ApFooter */
const ApFooter = React.createClass({

  // --------------------
  // Specs
  // --------------------

  propTypes: {},

  mixins: [],

  statics: {},

  getInitialState () {
    return {}
  },

  getDefaultProps () {
    return {}
  },

  render () {
    const s = this
    let { props } = s

    return (
      <footer className={ classnames('ap-footer', props.className) }>
        { props.children }
      </footer>
    )
  }
})

export default ApFooter
